"use client"
import Avatar from "@radui/ui/Avatar"

const AvatarExample = () => {
    return <Avatar.Root>
        <Avatar.Image src="https://i.pravatar.cc/1000" />
        <Avatar.Fallback>KL</Avatar.Fallback>
    </Avatar.Root>
}

export default AvatarExample;

scss.code:
.rad-ui-avatar{
    position: relative;
    width:36px;
    height:36px;
    border-radius: 100%;
    overflow: hidden;
    border: 1px solid var(--rad-ui-color-gray-600);
    display: flex;

    .rad-ui-avatar-image{
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        object-fit: cover;
    }

    .rad-ui-avatar-fallback{
        display: flex;
        align-items: center;
        font-size: 16px;
        justify-content: center;
        color: var(--rad-ui-color-accent-900);
        background-color: var(--rad-ui-color-accent-500);
        width: 100%;
        height:100%;
    }
}

